from web3 import Web3

class Polygon:
    def __init__(self):
        pass

polygon_rpc_url = "https://polygon-rpc.com"

interface = Web3(Web3.HTTPProvider(polygon_rpc_url))

if (interface.is_connected()):
    print (f"Connected to Polygon Mainnet at {polygon_rpc_url}.")
else:
    print(f"Connection to Polygon Mainnet failed.")

print("Loading Dream Token.")
