dream = interface.eth.contract(address=dream_address, abi=dream_abi)
print("Connected to Dream Token.")

def get_block_number() -> int:
    return interface.eth.block_number

def call_dream_allowance(owner_address:str, spender_address:str) -> float:
    return dream.functions.allowance(owner_address, spender_address).call() / 10**18

def call_dream_balance_of(account:str) -> float:
    return dream.functions.balanceOf(account).call() / 10**18

def call_dream_balance_of_at(account:str, snapshot_id:int) -> float:
    return dream.functions.balanceOfAt(account, snapshot_id).call() / 10**18

def call_dream_decimals() -> float:
    return dream.functions.decimals().call()

def call_dream_get_current_snapshot_id() -> int:
    return dream.functions.getCurrentSnapshotId().call()

def call_dream_name() -> str:
    return dream.functions.name().call()

def call_dream_symbol() -> str:
    return dream.functions.symbol().call()

def call_dream_total_supply() -> float:
    return dream.functions.totalSupply().call() / 10**18

def call_dream_total_supply_at(snapshot_id:int) -> float:
    return dream.functions.totalSupplyAt(snapshot_id).call() / 10**18